import axios from "axios";
import { BASE_URL } from '@/config'


class APIService {


    init() {
        console.log("APIService has been initiated")
    }

   async saveDesc(cardDesc, card_id) {
        var params = {
            card_id: card_id,
            cardDesc: cardDesc
        }
       this.baseRequest("board/addDueDescriptionToCard", params)
    }



    async baseRequest(path, params) {
        let fullURL = BASE_URL + path
        await axios.post(fullURL, params).then((response) => {
            if (response.data != null) {
                let data = response.data
                if (data.statusCode == 200) {
                    let resp = data.resp
                    console.log("card description update: ", resp)
                    return resp
                }
            }
        })
    }
}






export default new APIService()
