import { ref } from 'vue';
import {
// necessary extensions
Doc, Text, Paragraph, Heading, Bold, Underline, Italic, Strike, BulletList, OrderedList } from 'element-tiptap';

// editor extensions
// they will be added to menubar and bubble menu by the order you declare.

export default {
  __name: 'Editor',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const extensions = [Doc, Text, Paragraph, Heading.configure({
      level: 5
    }), Bold.configure({
      bubble: true
    }),
    // render command-button in bubble menu.
    Underline.configure({
      bubble: true,
      menubar: false
    }),
    // render command-button in bubble menu but not in menubar.
    Italic, Strike, BulletList, OrderedList];

    // editor's content
    const content = ref(`
    <h1>Heading</h1>
    <p>This Editor is awesome!</p>
  `);
    const __returned__ = {
      extensions,
      content,
      ref,
      get Doc() {
        return Doc;
      },
      get Text() {
        return Text;
      },
      get Paragraph() {
        return Paragraph;
      },
      get Heading() {
        return Heading;
      },
      get Bold() {
        return Bold;
      },
      get Underline() {
        return Underline;
      },
      get Italic() {
        return Italic;
      },
      get Strike() {
        return Strike;
      },
      get BulletList() {
        return BulletList;
      },
      get OrderedList() {
        return OrderedList;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
